import React from 'react';
import './App.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,Redirect
} from "react-router-dom";

import Home from './components/home.js'
import NavBar from './components/navigation.js' //exported component. 
import Noticias from './components/noticias.js'
export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {events: [], event: " "};
  }
//fetch all event on load of the page.
//maybe be better to load on request only. but all at once.
  componentDidMount=()=>{
    fetch('http://localhost:3000/api/v1/events', {
      method: 'GET', // or 'PUT'
      headers: {
        'Content-Type': 'application/json',
      },
        })
        .then((response) => response.json())
        .then((data) => {
        console.log('Success:', data);
        this.setState({events: data, event: data[0].deporte}) //respose will set the state for events heading.
        })
        .catch((error) => {
        console.error('Error:', error);
        });
  }
handleCategorySelection=(e)=>{
e.target.value
}

  render(){

    return (
    <div className="main-screen">
      <div>
      <NavBar event={this.state.events}/>
      </div>
      <Router>
        <div className='main'>
          <Switch>
            <Route exact path='/'><Redirect to="/home" /></Route>
            <Route path='/home'>
            <Home events={this.state.events} event={this.state.event}/>
            </Route>


          </Switch>
          </div>
      </Router>
      </div>

  );
  }
}
