import React ,{useState} from 'react'
import { Tabs , Tab, Card, Row, Col, Container,Modal,Button} from 'react-bootstrap';
import Profile from './userComponents/profile.js'
import Edit from './userComponents/edit.js'

export default class User extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            key: "profile"
        };
      }
      MyVerticallyCenteredModal=(props)=>{
        return (
          <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Modal heading
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <h4>Centered Modal</h4>
              <p>
                Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                consectetur ac, vestibulum at eros.
              </p>
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
          </Modal>
        );
      }
      componentDidMount=()=>{

      }
    render(){
        const [modalShow, setModalShow] = React.useState(false);
        return(
            <div>
                <MyVerticallyCenteredModal
                    show={modalShow}
                    onHide={() => setModalShow(false)}
                />
                <Container className="container-user">
                  <Row>
                    <Col>
                    <div>
                <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src="https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_960_720.png" />
                <Card.Body>
                    <Card.Title>Nombre</Card.Title>
                </Card.Body>
                </Card>
                </div>
                    </Col>
                    <Col>
                    <div>
                <Tabs
                    id="controlled-tab-"
                    activeKey={this.state.key}
                    onSelect={(e) => this.setState({key: e})}
                    >
                    <Tab eventKey="profile" title="Profile">
                        <Profile/>
                    </Tab>
                    <Tab eventKey="edit" title="Edit" >
                        <Edit/>
                    </Tab>
                </Tabs>
                </div>
                    </Col>
                </Row>

                </Container>
            </div>
        )
    }
}
