import React from 'react';
import './App.css';
import Main from './components/main.js'
import NavBar from './components/navigation.js'
import { Button } from 'react-bootstrap';
import fb from './assets/fb.png'

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      name: '',
      email: '',
      picture:'',
      status: 'Log in With Facebook'
    };
  }

  componentDidMount=()=>{
    window.fbAsyncInit = () =>{ //AppID
      window.FB.init({
        appId            : '2854741097907041',
        autoLogAppEvents : true,
        xfbml            : true,
        version          : 'v6.0'
      });
      window.FB.getLoginStatus((response) =>{
        console.log(response)
        if(response.status === "connected"){
            this.setState({status: 'Log out'})
        }
      });
    };

    (function(d, s, id) {                      // Load the SDK asynchronously
      var js, fjs = d.getElementsByTagName(s)[0];
      if (d.getElementById(id)) return;
      js = d.createElement(s); js.id = id;
      js.src = "https://connect.facebook.net/en_US/sdk.js";
      fjs.parentNode.insertBefore(js, fjs);
    }(document, 'script', 'facebook-jssdk')
    );

  }


  handleLogin=()=>{
    if(this.state.status === "Log in With Facebook"){
      window.FB.login(function(response) {
        console.log(response)
        if (response.authResponse) {
         console.log('Welcome!  Fetching your information.... ');
         window.FB.api('/me', {fields: "picture , name, email "}, function(resp) {
           console.log(resp);
         });
        } else {
         console.log('User cancelled login or did not fully authorize.');
        }
       });

    }else{
      window.FB.logout(function(response){
      });
    }
  }


  handleModalShow=()=>{
    this.setState({ modalShow: true})
    console.log("modal")
  }

  render(){

    return (
    <div >
      <NavBar 
        handleModalShow={this.handleModalShow}
      />
      <Main/>

      <Button onClick={this.handleLogin}>
        <img src={fb} width="37" alt='fb_logo'/>
        &nbsp;
        {this.state.status}
      </Button >
      </div> 

  );
  }
}
