import React , { useState }  from 'react';
import './App.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,Redirect
} from "react-router-dom";

import Home from './components/home.js'
import NavBar from './components/navigation.js' //exported component. 
import Noticias from './components/noticias.js'
import Events from './components/events.js'


const [currentEvent, setEvent] = useState(" ");
const [events, setEvents] = useState([]);

function App (){

//fetch all event on load of the page.
//maybe be better to load on request only. but all at once.
  componentDidMount=()=>{
    fetch('http://localhost:3000/api/v1/events', {
      method: 'GET', // or 'PUT'
      headers: {
        'Content-Type': 'application/json',
      },
        })
        .then((response) => response.json())
        .then((data) => {
        console.log('Success:', data);
        setEvents(data) //respose will set the state for events heading.
        })
        .catch((error) => {
        console.error('Error:', error);
        });
  }
handleCategorySelection=(e)=>{
  setEvent(e)
}

    return (
    <div className="main-screen">
      <div>
      <NavBar events={this.state.events} handleCategorySelection={this.handleCategorySelection}/>
      </div>
      <Router>
        <div className='main'>
          <Switch>
            <Route exact path='/'><Redirect to="/home" /></Route>
            <Route path='/home'>
            <Home events={this.state.events}/>
            </Route>
            <Route path={`/${currentEvent}`}>
            <Events events={this.state.events} event={currentEvent}/>
            </Route>
            <Route path='/noticias'>
            <Noticias events={this.state.events} event={this.state.currentEvent}/>
            </Route>
          </Switch>
          </div>
      </Router>
      </div>

  );

}

export default App;
