import React from 'react'
import { Button , CardGroup, Container, Row, Col } from 'react-bootstrap';
export default class Events extends React.Component{
    constructor(props){
        super(props);
        this.state = { event: "Eventos" };
      }
     componentDidMount=()=>{
         this.setState({event: this.props.events[0].deporte})
     } 
    render(){

        return(
            <div className="container-event">
                <h1>{this.state.event}</h1>
                <Container>
                    <Row>
                        <Col>
                  {this.props.events.map(event => 
                        <CardGroup >
                  <div className="card" key={event.id}>
                  <img className="card-img" src={event.images}  alt="CardBck"/>
                  <div className="card-img-overlay">
                    <h1>{event.title}</h1>
                  </div>

                    </div>
                    <Button className="vermas-Btn" variant="success">Ver mas</Button>
                </CardGroup>
                        )}
                        </Col>

                    </Row>
                </Container>

            </div>
        )
    }
}
